mapper.select:
SELECT CODE.CODE_VAL, CODE.CODE_VAL_DESC, USER.USER_ID, USER.USER_NM FROM TMB_USER USER LEFT OUTER JOIN TMB_CODE CODE ON USER.USER_CODE_VAL = CODE.CODE_VAL WHERE USER.USER_CODE_VAL != 'ATH999' AND USER.USER_DIV = #{userDiv}
AND USER.USER_ID = #{userId}
GROUP BY CODE.CODE_VAL, CODE.CODE_VAL_DESC ORDER BY CODE.CODE_VAL_DESC

SELECT RE.NUM, RE.EVT_SEQ, RE.EVT_PARTNER_CODE, RE.EVT_CLNT_CODE, RE.EVT_USER_NM, RE.EVT_USER_AGE, RE.EVT_USER_PH_NUM, RE.EVT_AR_NM, RE.EVT_STS_CD , RE.EVT_CLNT_NM, RE.EVT_PARTNER_NM, RE.EVT_STS_NM, RE.EVT_SURVEY1, RE.EVT_SURVEY2, RE.EVT_SURVEY3, RE.EVT_SURVEY4, RE.EVT_SURVEY5, RE.EVT_SURVEY6 , RE.EVT_DESC, RE.EVT_IP, RE.REG_DT, RE.REG_DT_EXCEL, RE.SURVEY_CNT FROM ( SELECT @ROWNUM:=@ROWNUM+1 AS NUM, EVT_SEQ , SUBSTRING(EVT_PARTNER_CODE, 1, 6) EVT_PARTNER_CODE , SUBSTRING(EVT_CLNT_CODE, 1, 6) EVT_CLNT_CODE , EVT_USER_NM, EVT_USER_AGE, EVT_USER_PH_NUM, EVT_AR_NM, EVT_STS_CD , (SELECT CODE_VAL_DESC FROM TMB_CODE WHERE CODE_VAL = SUBSTRING(EVT_CLNT_CODE, 1, 6)) EVT_CLNT_NM , (SELECT CODE_VAL_DESC FROM TMB_CODE WHERE CODE_VAL = SUBSTRING(EVT_PARTNER_CODE, 1, 6)) EVT_PARTNER_NM , (SELECT CODE_VAL_DESC FROM TMB_CODE WHERE CODE_VAL = EVT_STS_CD) EVT_STS_NM , EVT_SURVEY1, EVT_SURVEY2, EVT_SURVEY3, EVT_SURVEY4, EVT_SURVEY5, EVT_SURVEY6, EVT_DESC, EVT_IP , DATE_FORMAT(REG_DT, '%Y년 %m월 %d일') REG_DT , DATE_FORMAT(REG_DT, '%Y-%m-%d %H:%i') REG_DT_EXCEL , CASE WHEN EVT_SURVEY2 = '' THEN '1' WHEN EVT_SURVEY3 = '' THEN '2' WHEN EVT_SURVEY4 = '' THEN '3' WHEN EVT_SURVEY5 = '' THEN '4' WHEN EVT_SURVEY6 = '' THEN '5' ELSE '6' END SURVEY_CNT FROM TMB_EVENT , (SELECT @ROWNUM:=0) AS R WHERE 1=1
AND SUBSTRING(EVT_CLNT_CODE, 1 ,6) = #{client}
AND SUBSTRING(EVT_PARTNER_CODE, 1, 6) = #{partner}
AND EVT_STS_CD = #{rev}
AND EVT_USER_NM LIKE CONCAT('%', #{searchText}, '%')
AND EVT_USER_PH_NUM = #{searchText},
AND EVT_AR_NM LIKE CONCAT('%', #{searchText}, '%')
AND DATE_FORMAT(REG_DT, '%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(REG_DT, '%Y-%m-%d') <= #{endDate}
ORDER BY REG_DT_EXCEL ) RE ORDER BY RE.NUM DESC

mapper.update:
      UPDATE TMB_EVENT
         SET EVT_STS_CD = #{revModal}
           , EVT_IP = #{evtIp}

		   , EVT_CLNT_CODE = CONCAT(#{clientModal}, DATE_FORMAT(NOW(), '%Y%m%d%H%i'))
           , EVT_PARTNER_CODE = CONCAT(#{partnerModal}, DATE_FORMAT(NOW(), '%Y%m%d%H%i'))
           , EVT_USER_NM = #{evtUserNm}
           , EVT_USER_AGE = #{evtUserAge}
           , EVT_USER_PH_NUM = #{evtUserPhNum}
           , EVT_AR_NM = #{evtArNm}
           , EVT_SURVEY1 = #{evtSurvey1}
           , EVT_SURVEY2 = #{evtSurvey2}
           , EVT_SURVEY3 = #{evtSurvey3}
           , EVT_SURVEY4 = #{evtSurvey4}
           , EVT_SURVEY5 = #{evtSurvey5}
           , EVT_SURVEY6 = #{evtSurvey6}
           , EVT_DESC = #{evtDesc}

       WHERE EVT_SEQ = #{evtSeq}

mapper.delete:
DELETE FROM TMB_EVENT
 WHERE EVT_SEQ IN

