mapper.select:
SELECT USER_ID, USER_PW, USER_NM, USER_DIV, ( SELECT A.CODE_VAL_DESC FROM tmb_code A WHERE A.CODE_VAL = B.USER_DIV ) AS USER_DIV_DESC, USER_CODE_VAL, ( SELECT A.CODE_VAL_DESC FROM tmb_code A WHERE A.CODE_VAL = B.USER_CODE_VAL ) AS USER_CODE_VAL_DESC, USER_PH_NUM, USER_EVT_CODE, USER_STS, ( SELECT A.CODE_VAL_DESC FROM tmb_code A WHERE A.CODE_VAL = B.USER_STS ) AS USER_STS_DESC, DATE_FORMAT(REG_DT, '%Y-%m-%d %H:%i:%s') AS REG_DT FROM TMB_USER B WHERE 1=1
AND USER_ID = #{userId}
AND USER_DIV = #{userDiv}
AND USER_STS = #{userSts}

mapper.update:
UPDATE TMB_USER 
SET 
	USER_NM = #{userNm},
	USER_PW = #{userPw},
	USER_PH_NUM = #{userPhNum},
	USER_STS = #{userSts}
WHERE 1=1
	AND USER_ID = #{userId}
	AND USER_EVT_CODE = #{userEvtCode}

mapper.delete:
DELETE FROM TMB_USER 
WHERE 1=1
	AND USER_ID = #{userId}
	AND USER_EVT_CODE = #{userEvtCode}
